import React, { ReactElement } from "react";
interface Props {
    bgColor?: string;
    border?: boolean;
    content: string;
}

const BubbleChat = ({ bgColor, border, content }: Props): ReactElement => {
    return (
        <div
            className={`${bgColor}
            ${border && "border-2 border-neo-bg-B"}
             text-xxs rounded-md p-2  text-neo-light-grey `}
            data-testid="bubbleChat-body"
        >
            {content}
        </div>
    );
};

export default BubbleChat;

import React, { ReactElement } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { IconProp } from "@fortawesome/fontawesome-svg-core";

interface Props {
    className?: string;
    data?: string;
    disabled?: boolean;
    fCallback?: () => void;
    fontIcon?: IconProp;
    iconClassName?: string;
    svg?: ReactElement;
    svgClassName?: string;
    style?: React.CSSProperties;
    testId?: string;
    type?: "button" | "submit" | "reset";
}

const Button = ({
    className,
    data,
    disabled,
    fCallback,
    fontIcon,
    iconClassName,
    svg,
    svgClassName,
    style,
    testId,
    type = "button",
}: Props): ReactElement => {
    return (
        <button
            type={type}
            className={className}
            onClick={fCallback}
            data-testid={testId}
            disabled={disabled}
            style={style}
        >
            {svg && <div className={svgClassName}>{svg}</div>}
            {data}
            {fontIcon && (
                <div className={iconClassName}>
                    <FontAwesomeIcon icon={fontIcon} />
                </div>
            )}
        </button>
    );
};

export default Button;

import React, { ReactElement, useEffect, useState } from "react";
import { IconProp } from "@fortawesome/fontawesome-svg-core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

interface Props {
    active?: boolean;
    activeClassName?: string;
    activeData?: string;
    activeFontIcon?: IconProp;
    activeIconClassName?: string;
    disabled?: boolean;
    fCallback?: (data: boolean) => void;
    inactiveClassName?: string;
    inactiveData?: string;
    inactiveFontIcon?: IconProp;
    inactiveIconClassName?: string;
    testId?: string;
    type?: "button" | "submit" | "reset";
}

const ButtonSwitch = ({
    active = false,
    activeClassName,
    activeData,
    activeFontIcon,
    activeIconClassName,
    disabled,
    fCallback,
    inactiveClassName,
    inactiveData,
    inactiveFontIcon,
    inactiveIconClassName,
    testId,
    type = "button",
}: Props): ReactElement => {
    const [isActive, setIsActive] = useState(active);

    useEffect(() => {
        setIsActive(active);
    }, [active]);

    return (
        <button
            className={`flex items-center justify-center
            ${isActive ? activeClassName : inactiveClassName}
            rounded-lg`}
            onClick={() => {
                setIsActive(!isActive);
                fCallback && fCallback(isActive);
            }}
            data-testid={testId}
            disabled={disabled}
            type={type}
        >
            {isActive ? <div className="px-2">{activeData}</div> : <div className="px-2">{inactiveData}</div>}
            {activeFontIcon && inactiveFontIcon && (
                <div
                    className={`flex items-center justify-center m-1
                            ${isActive ? activeIconClassName : inactiveIconClassName}
                            rounded-lg`}
                >
                    <FontAwesomeIcon icon={isActive ? activeFontIcon : inactiveFontIcon} />
                </div>
            )}
        </button>
    );
};

export default ButtonSwitch;

import React, { ReactElement, ChangeEvent } from "react";

interface Props {
    checked?: boolean;
    data?: string;
    fCallBack?: (e: ChangeEvent<HTMLInputElement>) => void;
    name?: string;
    testId?: string;
    classNameInput?: string;
    classNameLabel?: string;
}

const Checkbox = ({ checked, data, fCallBack, name, testId, classNameInput, classNameLabel }: Props): ReactElement => {
    return (
        <>
            <input
                checked={checked}
                className={classNameInput}
                data-testid={testId}
                id={name}
                name={name}
                onChange={fCallBack}
                type="checkbox"
            />
            <label htmlFor={name} className={classNameLabel}>
                {data}
            </label>
        </>
    );
};

export default Checkbox;

import React, { ReactElement } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { IconProp } from "@fortawesome/fontawesome-svg-core";
import { faCircle } from "@fortawesome/free-solid-svg-icons";

interface Props {
    className?: string;
    fCallBack?: () => void;
    fontIcon: IconProp;
    redDot?: boolean;
    type?: string;
}

const Icon = ({ className, fCallBack, fontIcon, redDot, type }: Props): ReactElement => {
    switch (type) {
        case "iconLink":
            return (
                <div
                    className={`${className} p-4 text-neo-bg-B hover:text-neo-blue cursor-pointer text-4xl`}
                    onClick={fCallBack}
                    data-testid="icon-link-body"
                >
                    <FontAwesomeIcon icon={fontIcon} />
                </div>
            );
        case "placeholderInput":
            return (
                <div className="p-4 text-neo-bg-B text-4xl" data-testid="icon-placeholder-body">
                    <FontAwesomeIcon
                        icon={fontIcon}
                        className="pointer-events-none w-6 h-6 absolute top-12 transform -translate-y-1/2 left-3"
                    />
                </div>
            );
        case "iconWithRedDot":
            return (
                <div className={`${className} p-1 text-neo-bg-B text-4xl relative w-10`} data-testid="icon-reddot-body">
                    <FontAwesomeIcon icon={fontIcon} />
                    {redDot && (
                        <FontAwesomeIcon icon={faCircle} className="text-neo-red absolute top-0 right-0 text-xxs" />
                    )}
                </div>
            );
        default:
            return (
                <div
                    className={`${className} flex items-center justify-center`}
                    onClick={fCallBack}
                    data-testid="icon-default-body"
                >
                    <FontAwesomeIcon icon={fontIcon} />
                </div>
            );
    }
};

export default Icon;

import React, { ReactElement, useState, MouseEvent } from "react";
import { IconProp } from "@fortawesome/fontawesome-svg-core";

import Icon from "./icon";

interface Props {
    className?: string;
    component?: ReactElement;
    data: string;
    fCallback?: (e: MouseEvent) => void;
    fontIcon?: IconProp;
    fontIconClassName?: string;
    position?: string;
}

const Tooltip = ({
    className,
    component,
    data,
    fCallback,
    fontIcon,
    fontIconClassName,
    position,
}: Props): ReactElement => {
    const [showTooltip, setShowTooltip] = useState(false);

    switch (position) {
        case "top":
            position = "left-1/2 transform -translate-x-1/2 bottom-6";
            break;
        case "bottom":
            position = "left-1/2 transform -translate-x-1/2 top-6";
            break;
        default:
            position = "left-1/2 transform -translate-x-1/2 top-6";
            break;
    }

    return (
        <div
            className="text-neo-link cursor-pointer relative"
            onMouseEnter={(): void => setShowTooltip(true)}
            onMouseLeave={(): void => setShowTooltip(false)}
            data-testid="tooltip-body"
        >
            {component && component}
            {fontIcon && (
                <div onClick={(e) => fCallback && fCallback(e)} data-testid="tooltip-icon-body">
                    <Icon fontIcon={fontIcon} className={fontIconClassName} />
                </div>
            )}
            <div
                data-testid="tooltip-bubble"
                className={`
                    ${showTooltip ? "" : "hidden"} 
                    ${position} 
                    ${className}
                    w-max max-w-xxs absolute z-50
                `}
            >
                {data}
            </div>
        </div>
    );
};

export default Tooltip;

import React, { ReactElement, useRef, useEffect } from "react";
import { faCheck, faExclamationTriangle, faUndo } from "@fortawesome/free-solid-svg-icons";

import Icon from "./icon";
import Tooltip from "./tooltip";

interface Props {
    errorMessage?: string;
    isCancelable?: boolean;
    isCooldown: boolean;
    isError?: boolean | undefined;
    isSuccess: boolean;
    isUpdateField: boolean;
    onClickCallback: () => void;
    positionClassname?: string;
    timer?: number;
    trigger?: boolean;
}

const Dot = ({
    errorMessage,
    isCancelable,
    isCooldown,
    isError,
    isSuccess,
    isUpdateField,
    onClickCallback,
    positionClassname,
    timer = 5000,
    trigger,
}: Props): ReactElement => {
    const radius = 45;
    const animationRef = useRef<SVGAnimateElement>(null);

    function getCircleCircumference(radius: number) {
        return 2 * Math.PI * radius;
    }

    useEffect(() => {
        animationRef.current?.beginElement();
    }, [trigger]);

    return (
        <div
            className={`${positionClassname} relative w-5 p-1 text-center rounded-full text-white ${
                (isCancelable || isError) && "bg-neo-red cursor-pointer"
            } ${isSuccess && "bg-neo-green"}`}
            onClick={(): void => {
                isCancelable && onClickCallback();
            }}
            data-testid="dot-body"
        >
            {isCooldown && (
                <svg
                    className="w-6 h-6 absolute left-1/2 top-1/2 transform -translate-x-1/2 -translate-y-1/2"
                    id="circle"
                    version="1.1"
                    viewBox="0 0 100 100"
                    x="0px"
                    xmlns="http://www.w3.org/2000/svg"
                    xmlnsXlink="http://www.w3.org/1999/xlink"
                    xmlSpace="preserve"
                    y="0px"
                >
                    <circle
                        cx="50"
                        cy="50"
                        fill="none"
                        r={radius}
                        stroke="#7FEF7F"
                        strokeDasharray={getCircleCircumference(radius)}
                        strokeLinecap="round"
                        strokeMiterlimit="0"
                        strokeWidth="10"
                        transform="rotate(-90 ) translate(-100 0)"
                    >
                        <animate
                            attributeName="stroke-dashoffset"
                            dur={`${timer / 1000}s`}
                            ref={animationRef}
                            values={`${getCircleCircumference(radius)};0`}
                        ></animate>
                    </circle>
                </svg>
            )}
            {isCancelable && (
                <div className={`text-xs relative `}>
                    {!isError && <Icon fontIcon={faUndo} />}
                    {isError && errorMessage && (
                        <Tooltip
                            className="bg-neo-red text-white px-4 py-1 rounded"
                            data={errorMessage}
                            fontIcon={faExclamationTriangle}
                            fontIconClassName="text-white"
                        />
                    )}
                </div>
            )}
            {!isUpdateField && isError && errorMessage && (
                <div className="text-xs">
                    <Tooltip
                        className="bg-neo-red text-white px-4 py-1 rounded"
                        data={errorMessage}
                        fontIcon={faExclamationTriangle}
                        fontIconClassName="text-white"
                    />
                </div>
            )}
            {isSuccess && (
                <div className="text-xs">
                    <Icon fontIcon={faCheck} />
                </div>
            )}
        </div>
    );
};
export default Dot;

type State = {
    isCancelable: boolean;
    isCooldown: boolean;
    isSuccess: boolean;
    previous: string | number | number[] | Date | undefined;
    timeoutId: NodeJS.Timeout | undefined;
    trigger: boolean;
    updated: string | number | number[] | Date | undefined;
};

type Action =
    | { type: "CANCEL_UPDATE" }
    | { type: "CLEAR_SUCCESS" }
    | { type: "DEBUG"; payload: number | number[] | string | Date }
    | { type: "HANDLE_DUPLICATE"; payload: { value: number; isUpdateField: boolean } }
    | { type: "REMOVE_ITEM"; payload: { value: number[]; isUpdateField: boolean } }
    | { type: "RESET"; payload: number | number[] | string | Date }
    | { type: "SET_TIMEOUT"; payload: NodeJS.Timeout }
    | { type: "SHOW_DOT" }
    | { type: "UPDATE_SUCCESS" }
    | { type: "UPDATING"; payload: string | number | Date };

export default function inputReducer(state: State, action: Action): State {
    switch (action.type) {
        case "CANCEL_UPDATE":
            return { ...state, isCooldown: false, isSuccess: false, isCancelable: false, updated: state.previous };
        case "CLEAR_SUCCESS":
            return { ...state, isSuccess: false };
        case "DEBUG":
            // eslint-disable-next-line no-console
            console.log(action.payload);
            return { ...state };
        case "HANDLE_DUPLICATE":
            return {
                ...state,
                isCancelable: action.payload.isUpdateField,
                isCooldown: action.payload.isUpdateField,
                isSuccess: false,
                trigger: !state.trigger,
                updated: [...(state.updated as number[]), action.payload.value],
            };
        case "REMOVE_ITEM":
            return {
                ...state,
                isCancelable: action.payload.isUpdateField,
                isCooldown: action.payload.isUpdateField,
                trigger: !state.trigger,
                updated: action.payload.value,
            };
        case "RESET":
            return {
                isCancelable: false,
                isCooldown: false,
                isSuccess: false,
                previous: action.payload,
                timeoutId: undefined,
                trigger: false,
                updated: action.payload,
            };
        case "SET_TIMEOUT":
            return { ...state, timeoutId: action.payload };
        case "SHOW_DOT":
            return { ...state, isCancelable: true, isCooldown: false, isSuccess: false };
        case "UPDATE_SUCCESS":
            return { ...state, isCancelable: false, isCooldown: false, isSuccess: true, previous: state.updated };
        case "UPDATING":
            return {
                ...state,
                isCancelable: true,
                isCooldown: true,
                isSuccess: false,
                trigger: !state.trigger,
                updated: action.payload,
            };
        default:
            return state;
    }
}

import React, { ReactElement, useEffect, useReducer, useRef, useState } from "react";
import { UseFormSetValue, UseFormRegister, FieldValues, UseFormClearErrors } from "react-hook-form";
import { CKEditor } from "@ckeditor/ckeditor5-react";
import ClassicEditor from "@ckeditor/ckeditor5-build-classic";

import inputReducer from "../../utils/reducers/inputReducer";
import Dot from "../dot";

interface Props {
    clearErrors?: UseFormClearErrors<FieldValues>;
    defaultValue?: string;
    dotPosition?: string;
    errorMessage?: string;
    isError?: boolean;
    isUpdateField?: boolean;
    refForm: string;
    register?: UseFormRegister<FieldValues>;
    required?: boolean;
    setValue?: UseFormSetValue<FieldValues>;
    targetId?: number | undefined;
    timerSetting?: number;
    updateFunction?: (refForm: string, value: string) => void;
}

const EditableTextarea = ({
    clearErrors,
    defaultValue = "",
    dotPosition,
    errorMessage,
    isError,
    isUpdateField = false,
    refForm,
    register,
    required,
    setValue,
    targetId,
    timerSetting = 5000,
    updateFunction,
}: Props): ReactElement => {
    const toolbarConfig = [
        "heading",
        "bold",
        "italic",
        "link",
        "outdent",
        "indent",
        "bulletedList",
        "numberedList",
        // For now it's not working as intended
        //"insertTable",

        //variable for file uploadin
        // "uploadImage",
        "blockQuote",
        "undo",
        "redo",
    ];

    const [state, dispatch] = useReducer(inputReducer, {
        isCancelable: false,
        isCooldown: false,
        isSuccess: false,
        previous: getHTMLValue(defaultValue),
        timeoutId: undefined,
        trigger: false,
        updated: getHTMLValue(defaultValue),
    });

    const [key, setKey] = useState(0);

    const isLastMount = useRef(false);

    function getHTMLValue(e: string): string {
        return e.replace(/&lt;/g, "<").replace(/&gt;/g, ">");
    }

    useEffect(() => {
        register && register(refForm, { required });
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    useEffect(() => {
        // force unmount
        setKey(key + 1);

        dispatch({ type: "RESET", payload: getHTMLValue(defaultValue) as string });
        setValue && setValue(refForm, getHTMLValue(defaultValue));
        return () => {
            isLastMount.current = true;
        };
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [targetId]);

    useEffect(() => {
        if (isUpdateField && state.updated && state.updated !== state.previous) {
            const newTimeout = setTimeout(() => {
                if (updateFunction && state.updated) {
                    updateFunction(refForm, state.updated as string);
                    dispatch({ type: "UPDATE_SUCCESS" });
                    setTimeout(() => {
                        dispatch({ type: "CLEAR_SUCCESS" });
                    }, 3000);
                }
            }, timerSetting);
            dispatch({ type: "SET_TIMEOUT", payload: newTimeout });
            return () => {
                if (isLastMount.current) {
                    clearTimeout(newTimeout);
                    updateFunction && updateFunction(refForm, state.updated as string);
                    isLastMount.current = false;
                }
            };
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [state.updated, state.previous]);

    return (
        <div className="flex w-full" key={key} data-testid="editableTextArea-body">
            <CKEditor
                config={{
                    toolbar: toolbarConfig,
                }}
                data={state.updated as string}
                editor={ClassicEditor}
                id="editor"
                onBlur={(e, editor): void => {
                    const data = editor.getData();
                    if (isUpdateField && state.previous !== data && !isError) {
                        dispatch({ type: "UPDATING", payload: data });
                        if (state.timeoutId) {
                            clearTimeout(state.timeoutId);
                        }
                    }
                }}
                onChange={(editor): void => {
                    const data = editor.getData();
                    setValue && setValue(refForm, data, { shouldValidate: true });
                    if (isUpdateField) {
                        if (state.previous !== data && !isLastMount.current) {
                            dispatch({ type: "SHOW_DOT" });
                        } else {
                            dispatch({ type: "CANCEL_UPDATE" });
                        }
                        if (state.timeoutId) {
                            clearTimeout(state.timeoutId);
                        }
                    }
                }}
            />
            <div className="w-5 mx-3 mt-2">
                {(isError || state.isCancelable || state.isSuccess) && (
                    <Dot
                        errorMessage={errorMessage}
                        isCancelable={state.isCancelable}
                        isCooldown={state.isCooldown}
                        isError={isError}
                        isSuccess={state.isSuccess}
                        isUpdateField={isUpdateField}
                        onClickCallback={(): void => {
                            if (setValue && clearErrors) {
                                setValue(refForm, state.previous);
                                clearErrors();
                            }
                            if (state.timeoutId) {
                                clearTimeout(state.timeoutId);
                            }
                            dispatch({ type: "CANCEL_UPDATE" });
                        }}
                        positionClassname={dotPosition}
                        trigger={state.trigger}
                    />
                )}
            </div>
        </div>
    );
};

export default EditableTextarea;

import React, { ReactElement } from "react";

interface Props {
    bgColor?: string;
    isNotif?: boolean;
    isSelected?: boolean;
    opacity?: string;
    type?: string;
    strokeColor?: string;
}

const Hexagon = ({ bgColor, isNotif, isSelected, opacity, type, strokeColor }: Props): ReactElement => {
    function transformOpacity(): string {
        if (opacity && opacity !== "") {
            return (parseInt(opacity) / 100).toString();
        }
        return "1";
    }

    switch (type) {
        case "ticket":
            return (
                <svg version="1.1" viewBox="-23 -15 220 230" data-testid="hexagonTicket-svg">
                    <path
                        //This line draw the hexagon in the container SVG it includes the shape itself, and the rounding which is applied to the hexagon.
                        d="M78.80 4.50Q86.60 0 94 4.50L165.41 45.5Q173.21 50 173.21 59L173.21 141Q173.21 150 165.42 154.5L94.40 195.5Q86.61 200 78.81 195.5L7.80 154.5Q0 150 0 141L0 59Q0 50 7.80 45.5Z"
                        fill={bgColor ? bgColor : "#172f4b"}
                        fillOpacity={transformOpacity()}
                        stroke={"#172f4b"}
                        strokeLinejoin="round"
                        strokeWidth="10"
                    ></path>
                </svg>
            );
        case "filter":
            return (
                <svg version="1.1" viewBox="-23 -15 220 230" data-testid="hexagonFilter-svg">
                    <path
                        d="M69.28 9.998Q86.60 0 103.92 9.998L155.88 40Q173.20 50 173.20 70L173.20 130Q173.20 150 155.88 160L103.92 190Q86.60 200 69.28 190L17.32 160Q0 150 0 130L0 70Q0 50 17.32 40Z"
                        fill={bgColor ? bgColor : "#2fa8fc"}
                        fillOpacity="1"
                        stroke={bgColor ? bgColor : "#2fa8fc"}
                        strokeLinejoin="round"
                        strokeWidth="10"
                        style={isNotif ? { filter: "drop-shadow(#ff0000 0px 0px 10px)" } : undefined}
                    ></path>
                </svg>
            );
        case "rotate":
            return (
                <div className="transform rotate-90" data-testid="hexagonRotate-svg">
                    <svg version="1.1" viewBox="-23 -15 220 230">
                        <path
                            d="M78.80 4.50Q86.60 0 94 4.50L165.41 45.5Q173.21 50 173.21 59L173.21 141Q173.21 150 165.42 154.5L94.40 195.5Q86.61 200 78.81 195.5L7.80 154.5Q0 150 0 141L0 59Q0 50 7.80 45.5Z"
                            fill="#7eaab7"
                            fillOpacity="1"
                            stroke="#15304b"
                            strokeLinejoin="round"
                            strokeWidth="10"
                            style={{ filter: "drop-shadow(#ff0000 0px 0px 10px)" }}
                        ></path>
                    </svg>
                </div>
            );
        case "leftHalf":
            return (
                <svg version="1.1" viewBox="-65 -2 150 230" data-testid="hexagonLeftHalf-svg">
                    <path
                        //This line draw the hexagon in the container SVG it includes the shape itself, and the rounding which is applied to the hexagon.
                        d="M78.80 4.50Q86.60 0 94 4.50L165.41 45.5Q173.21 50 173.21 59L173.21 141Q173.21 150 165.42 154.5L94.40 195.5Q86.61 200 78.81 195.5L7.80 154.5Q0 150 0 141L0 59Q0 50 7.80 45.5Z"
                        fill={bgColor ? bgColor : "#2fa8fc"}
                        fillOpacity="1"
                        stroke={bgColor ? bgColor : "#2fa8fc"}
                        strokeLinejoin="round"
                        strokeWidth="10"
                    ></path>
                </svg>
            );
        case "rightHalf":
            return (
                <svg version="1.1" viewBox="90 -2 150 230" data-testid="hexagonRightHalf-svg">
                    <path
                        //This line draw the hexagon in the container SVG it includes the shape itself, and the rounding which is applied to the hexagon.
                        d="M78.80 4.50Q86.60 0 94 4.50L165.41 45.5Q173.21 50 173.21 59L173.21 141Q173.21 150 165.42 154.5L94.40 195.5Q86.61 200 78.81 195.5L7.80 154.5Q0 150 0 141L0 59Q0 50 7.80 45.5Z"
                        fill={bgColor ? bgColor : "#2fa8fc"}
                        fillOpacity="1"
                        stroke={bgColor ? bgColor : "#2fa8fc"}
                        strokeLinejoin="round"
                        strokeWidth="10"
                    ></path>
                </svg>
            );
        default:
            return (
                <svg version="1.1" viewBox="-23 -15 220 230" data-testid="hexagonDefault-svg">
                    <path
                        d="M78.80 4.50Q86.60 0 94 4.50L165.41 45.5Q173.21 50 173.21 59L173.21 141Q173.21 150 165.42 154.5L94.40 195.5Q86.61 200 78.81 195.5L7.80 154.5Q0 150 0 141L0 59Q0 50 7.80 45.5Z"
                        fill={bgColor && bgColor}
                        fillOpacity={bgColor ? "1" : "0"}
                        strokeLinejoin="round"
                        stroke={strokeColor ? (isSelected ? strokeColor : "#172f4b") : "#172f4b"}
                        strokeWidth="10"
                    ></path>
                </svg>
            );
    }
};

export default Hexagon;

import React, { ReactElement } from "react";

interface Props {
    color: string;
    ticketNumber: number;
}

const HexaPill = ({ color, ticketNumber }: Props): ReactElement => {
    return (
        <div className="h-12" data-testid="hexaPill-body">
            <svg version="1.1" width="45" height="45" viewBox="-23 -15 220 230">
                <path
                    d="M78.80 4.50Q86.60 0 94 4.50L165.41 45.5Q173.21 50 173.21 59L173.21 141Q173.21 150 165.42 154.5L94.40 195.5Q86.61 200 78.81 195.5L7.80 154.5Q0 150 0 141L0 59Q0 50 7.80 45.5Z"
                    fillOpacity="0"
                    stroke={color}
                    strokeLinejoin="round"
                    strokeWidth="6"
                ></path>
                <text x="40%" y="48%" fontSize="60" dominantBaseline="middle" textAnchor="middle" fill={color}>
                    {ticketNumber}
                </text>
            </svg>
        </div>
    );
};

export default HexaPill;

