    errorMessage,
    isError,
    isUpdateField = false,
    items,
    label,
    refForm,
    register,
    setValue,
    targetId,
    timerSetting = 5000,
    updateFunction,
}: Props): ReactElement => {
    const [state, dispatch] = useReducer(inputReducer, {
        isCancelable: false,
        isCooldown: false,
        isSuccess: false,
        previous: defaultItems,
        timeoutId: undefined,
        trigger: false,
        updated: defaultItems,
    });

    const selectInput = useRef<HTMLSelectElement>(null);
    const isInitialMount = useRef(true);
    const isLastMount = useRef(false);

    function areArraysEqual(previous: number[], updated: number[]) {
        if (previous.length !== updated.length) {
            return false;
        }

        for (let i = 0; i < previous.length; ++i) {
            if (previous[i] !== updated[i]) return false;
        }
        return true;
    }

    function handleAddItem(e: ChangeEvent<HTMLSelectElement>) {
        const value = parseInt(e.target.value);
        // add items only if it is not already added and if it exist in the provided items
        if (
            !(state.updated as number[]).includes(value) &&
            (activeItems ? activeItems : items).some((element) => element.id === value) &&
            !(value === -1)
        ) {
            dispatch({ type: "HANDLE_DUPLICATE", payload: { value, isUpdateField } });
        }
    }

    useEffect(() => {
        register && register(refForm);
        dispatch({ type: "RESET", payload: defaultItems as number[] });
        setValue && setValue(refForm, defaultItems);
        if (selectInput.current) {
            selectInput.current.value = "-1";
        }
        return () => {
            isLastMount.current = true;
        };
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [targetId]);

    useEffect(() => {
        setValue && setValue(refForm, state.updated);
        if (isInitialMount.current) {
            isInitialMount.current = false;
        } else {
            if (isUpdateField) {
                if (!areArraysEqual(state.previous as number[], state.updated as number[])) {
                    const newTimeout = setTimeout((): void => {
                        if (updateFunction) {
                            updateFunction(refForm, state.updated as number[]);
                            dispatch({ type: "UPDATE_SUCCESS" });
                            setTimeout(() => {
                                dispatch({ type: "CLEAR_SUCCESS" });
                            }, 3000);
                        }
                    }, timerSetting);
                    dispatch({ type: "SET_TIMEOUT", payload: newTimeout });
                    return () => {
                        if (isLastMount.current) {
                            clearTimeout(newTimeout);
                            updateFunction && updateFunction(refForm, state.updated as number[]);
                            isLastMount.current = false;
                        }
                    };
                } else if (!state.isSuccess) {
                    dispatch({ type: "CANCEL_UPDATE" });
                }
            }
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [state.updated, state.previous]);

    return (
        <div className="w-full flex" data-testid="inputMultipleSelect-body">
            <div className="w-full">
                <label className="flex justify-between items-center w-full">
                    {label && <div className="text-white text-xs font-bold w-2/4">{label}</div>}
                    <select
                        className="w-full bg-neo-bg-B p-2 rounded text-white truncate"
                        onChange={(e): void => {
                            if (state.timeoutId) {
                                clearTimeout(state.timeoutId);
                            }
                            handleAddItem(e);
                        }}
                        ref={selectInput}
                    >
                        <option value={-1} key={-1}>
                            - - -
                        </option>
                        {(activeItems ? activeItems : items).map((opt) => (
                            <option key={opt.id} value={opt.id}>
                                {opt.value}
                            </option>
                        ))}
                    </select>
                </label>
                {(state.updated as number[]).map((id, key) => {
                    const value = items.find((item) => id === item.id);
                    return (
                        <div className="flex bg-neo-bg-B my-1 rounded justify-between" key={key}>
                            <p className="m-2 text-white">{value?.value}</p>
                            <button
                                className="m-2 text-neo-red"
                                onClick={(): void => {
                                    if (state.timeoutId) {
                                        clearTimeout(state.timeoutId);
                                    }
                                    dispatch({
                                        type: "REMOVE_ITEM",
                                        payload: {
                                            value: (state.updated as number[]).filter((item) => item !== value?.id),
                                            isUpdateField,
                                        },
                                    });
                                    if (selectInput.current) {
                                        selectInput.current.value = "-1";
                                    }
                                }}
                                type="button"
                                value={value?.id}
                            >
                                <FontAwesomeIcon icon={faTrashAlt} />
                            </button>
                        </div>
                    );
                })}
            </div>

            <div className="mx-3 mt-2 w-6">
                {(isError || state.isCancelable || state.isSuccess) && (
                    <Dot
                        errorMessage={errorMessage}
                        isCancelable={state.isCancelable}
                        isCooldown={state.isCooldown}
                        isError={isError}
                        isSuccess={state.isSuccess}
                        isUpdateField={isUpdateField}
                        onClickCallback={(): void => {
                            if (state.timeoutId) {
                                clearTimeout(state.timeoutId);
                            }
                            dispatch({ type: "CANCEL_UPDATE" });
                            if (selectInput.current) {
                                selectInput.current.value = "-1";
                            }
                        }}
                        positionClassname={dotPosition}
                        trigger={state.trigger}
                    />
                )}
            </div>
        </div>
    );
};

export default InputMultipleSelect;

import React, { ReactElement, useEffect } from "react";
import { FieldValues, UseFormRegister, UseFormSetValue } from "react-hook-form";

interface Props {
    className?: string;
    data: { label: string; value: string }[];
    defaultValue?: string;
    label?: string;
    labelClassName?: string;
    onChangeCallBack?: (e: string) => void;
    optionClassName?: string;
    placeholder?: string;
    refForm?: string;
    register?: UseFormRegister<FieldValues>;
    required?: boolean;
    selectClassName?: string;
    setValue?: UseFormSetValue<FieldValues>;
}

const InputSimpleSelect = ({
    className,
    data,
    defaultValue,
    label,
    labelClassName,
    onChangeCallBack,
    optionClassName,
    placeholder,
    refForm,
    register,
    required,
    selectClassName,
    setValue,
}: Props): ReactElement => {
    const inputRegister =
        refForm &&
        register &&
        register(refForm, {
            required,
            validate: (value) => (required ? value !== "" : true),
        });

    useEffect(() => {
        refForm && setValue && setValue(refForm, defaultValue);
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    return (
        <label className={`flex items-center w-full my-1 ${className}`} data-testid="inputSimpleSelect-body">
            {label && <div className={`text-white text-xs font-bold w-2/4 ${labelClassName}`}>{label}</div>}
            <select
                {...inputRegister}
                className={`w-full bg-neo-bg-B p-2 rounded truncate text-white  ${selectClassName}`}
                defaultValue={defaultValue}
                onChange={(e): void => {
                    onChangeCallBack && onChangeCallBack(e.target.value);
                    refForm && inputRegister && inputRegister.onChange(e);
                }}
            >
                {defaultValue === undefined && (
                    <option className={`${optionClassName}`} value={""} key={-1}>
                        {placeholder}
                    </option>
                )}
                {data
                    ? data.map((data, key) => (
                          <option className={`${optionClassName}`} key={key} value={data.value}>
                              {data.label}
                          </option>
                      ))
                    : null}
            </select>
        </label>
    );
};

export default InputSimpleSelect;

import React, { ReactElement, useEffect, useReducer, useRef } from "react";
import { FieldValues, UseFormRegister, UseFormSetValue } from "react-hook-form";

import Dot from "../dot";
import inputReducer from "../../utils/reducers/inputReducer";

interface Props {
    defaultValue?: string;
    dotPosition?: string;
    errorMessage?: string;
    isError?: boolean;
    isUpdateField?: boolean;
    label?: string;
    onChangeCallBack?: () => void;
    placeholder?: string;
    refForm: string;
    register?: UseFormRegister<FieldValues>;
    required?: boolean;
    setValue?: UseFormSetValue<FieldValues>;
    targetId?: number | undefined;
    timerSetting?: number;
    updateFunction?: (refForm: string, value: string) => void;
}

const InputTextarea = ({
    defaultValue,
    dotPosition,
    errorMessage,
    isError,
    isUpdateField = false,
    label,
    onChangeCallBack,
    placeholder,
    refForm,
    register,
    required,
    setValue,
    targetId,
    timerSetting = 5000,
    updateFunction,
}: Props): ReactElement => {
    const [state, dispatch] = useReducer(inputReducer, {
        isCancelable: false,
        isCooldown: false,
        isSuccess: false,
        previous: defaultValue,
        timeoutId: undefined,
        trigger: false,
        updated: defaultValue,
    });
    const isLastMount = useRef(false);

    useEffect(() => {
        dispatch({ type: "RESET", payload: defaultValue as string });
        setValue && setValue(refForm, defaultValue);
        return () => {
            isLastMount.current = true;
        };
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [targetId]);

    useEffect(() => {
        if (isUpdateField && state.updated && state.updated !== state.previous) {
            const newTimeout = setTimeout((): void => {
                if (updateFunction && state.updated) {
                    updateFunction(refForm, state.updated as string);
                    dispatch({ type: "UPDATE_SUCCESS" });
                    setTimeout(() => {
                        dispatch({ type: "CLEAR_SUCCESS" });
                    }, 3000);
                }
            }, timerSetting);
            dispatch({ type: "SET_TIMEOUT", payload: newTimeout });
            return () => {
                if (isLastMount.current) {
                    clearTimeout(newTimeout);
                    updateFunction && updateFunction(refForm, state.updated as string);
                    isLastMount.current = false;
                }
            };
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [state.updated, state.previous]);
    const inputRegister = register && register(refForm, { required });
    return (
        <div className="w-full h-52 flex text-white" data-testid="inputTextarea-body">
            <label className="w-full h-full">
                <div className="text-sm font-bold mr-2 mb-4">{label}</div>
                <textarea
                    {...(register && register(refForm, { required }))}
                    className="w-full h-5/6 bg-neo-bg-B rounded border-2 border-neo-light-grey px-2 py-1 "
                    defaultValue={defaultValue}
                    onBlur={(e): void => {
                        if (isUpdateField && state.previous !== e.target.value && !isError) {
                            dispatch({ type: "UPDATING", payload: e.target.value });
                            if (state.timeoutId) {
                                clearTimeout(state.timeoutId);
                            }
                        }
                    }}
                    onChange={(e): void => {
                        onChangeCallBack && onChangeCallBack();
                        inputRegister && inputRegister.onChange(e);
                        if (isUpdateField) {
                            if (state.previous !== e.target.value) {
                                dispatch({ type: "SHOW_DOT" });
                            } else {
                                dispatch({ type: "CANCEL_UPDATE" });
                            }
                            if (state.timeoutId) {
                                clearTimeout(state.timeoutId);
                            }
                        }
                    }}
                    placeholder={placeholder}
                ></textarea>
            </label>
            <div className="w-5">
                {(isError || state.isCancelable || state.isSuccess) && (
                    <Dot
                        errorMessage={errorMessage}
                        isCancelable={state.isCancelable}
                        isCooldown={state.isCooldown}
                        isError={isError}
                        isSuccess={state.isSuccess}
                        isUpdateField={isUpdateField}
                        onClickCallback={(): void => {
                            if (setValue && state.previous) {
                                setValue(refForm, state.previous);
                            }
                            if (state.timeoutId) {
                                clearTimeout(state.timeoutId);
                            }
                            dispatch({ type: "CANCEL_UPDATE" });
                        }}
                        positionClassname={dotPosition}
                        trigger={state.trigger}
                    />
                )}
            </div>
        </div>
    );
};

export default InputTextarea;

