type State = {
    isCancelable: boolean;
    isCooldown: boolean;
    isSuccess: boolean;
    previous: string | number | number[] | Date | undefined;
    timeoutId: NodeJS.Timeout | undefined;
    trigger: boolean;
    updated: string | number | number[] | Date | undefined;
};

type Action =
    | { type: "CANCEL_UPDATE" }
    | { type: "CLEAR_SUCCESS" }
    | { type: "DEBUG"; payload: number | number[] | string | Date }
    | { type: "HANDLE_DUPLICATE"; payload: { value: number; isUpdateField: boolean } }
    | { type: "REMOVE_ITEM"; payload: { value: number[]; isUpdateField: boolean } }
    | { type: "RESET"; payload: number | number[] | string | Date }
    | { type: "SET_TIMEOUT"; payload: NodeJS.Timeout }
    | { type: "SHOW_DOT" }
    | { type: "UPDATE_SUCCESS" }
    | { type: "ON_CHANGE"; payload: string }
    | { type: "UPDATING"; payload: string | number | Date };

export default function inputReducer(state: State, action: Action): State {
    switch (action.type) {
        case "CANCEL_UPDATE":
            return { ...state, isCooldown: false, isSuccess: false, isCancelable: false, updated: state.previous };
        case "CLEAR_SUCCESS":
            return { ...state, isSuccess: false };
        case "DEBUG":
            // eslint-disable-next-line no-console
            console.log(action.payload);
            return { ...state };
        case "HANDLE_DUPLICATE":
            return {
                ...state,
                isCancelable: action.payload.isUpdateField,
                isCooldown: action.payload.isUpdateField,
                isSuccess: false,
                trigger: !state.trigger,
                updated: [...(state.updated as number[]), action.payload.value],
            };
        case "REMOVE_ITEM":
            return {
                ...state,
                isCancelable: action.payload.isUpdateField,
                isCooldown: action.payload.isUpdateField,
                trigger: !state.trigger,
                updated: action.payload.value,
            };
        case "RESET":
            return {
                isCancelable: false,
                isCooldown: false,
                isSuccess: false,
                previous: action.payload,
                timeoutId: undefined,
                trigger: false,
                updated: action.payload,
            };
        case "SET_TIMEOUT":
            return { ...state, timeoutId: action.payload };
        case "SHOW_DOT":
            return { ...state, isCancelable: true, isCooldown: false, isSuccess: false };
        case "UPDATE_SUCCESS":
            return { ...state, isCancelable: false, isCooldown: false, isSuccess: true, previous: state.updated };
        case "UPDATING":
            return {
                ...state,
                isCancelable: true,
                isCooldown: true,
                isSuccess: false,
                trigger: !state.trigger,
                updated: action.payload,
            };
        case "ON_CHANGE":
            return {
                ...state,
                updated: action.payload,
            };
        default:
            return state;
    }
}

import React, { ReactElement, useEffect, useReducer, useRef } from "react";
import { FieldValues, UseFormClearErrors, UseFormRegister, UseFormSetValue } from "react-hook-form";

import inputReducer from "../../utils/reducers/inputReducer";
import Dot from "../dot";

interface Props {
    clearErrors?: UseFormClearErrors<FieldValues>;
    defaultValue?: string;
    disabled?: boolean;
    dotPosition?: string;
    errorMessage?: string;
    inputClassName?: string;
    isError?: boolean;
    isUpdateField?: boolean;
    label?: string;
    className?: string;
    onBlurCallBack?: () => void;
    onChangeCallBack?: (e: string) => void;
    onFocusCallBack?: () => void;
    pattern?: string;
    placeholder?: string;
    refForm: string;
    register?: UseFormRegister<FieldValues>;
    required: boolean;
    setValue?: UseFormSetValue<FieldValues>;
    targetId?: number | undefined;
    timerSetting?: number;
    typeInput: string;
    updateFunction?: (refForm: string, value: string) => void;
}

const Input = ({
    clearErrors,
    defaultValue,
    disabled,
    dotPosition,
    errorMessage,
    inputClassName,
    isError,
    isUpdateField = false,
    label,
    className,
    onBlurCallBack,
    onChangeCallBack,
    onFocusCallBack,
    pattern,
    placeholder,
    refForm,
    register,
    required,
    setValue,
    targetId,
    timerSetting = 5000,
    typeInput,
    updateFunction,
}: Props): ReactElement => {
    const [state, dispatch] = useReducer(inputReducer, {
        isCancelable: false,
        isCooldown: false,
        isSuccess: false,
        previous: defaultValue,
        timeoutId: undefined,
        trigger: false,
        updated: defaultValue,
    });

    const isLastMount = useRef(false);

    useEffect(() => {
        dispatch({ type: "RESET", payload: defaultValue as string });
        setValue && setValue(refForm, defaultValue);
        return () => {
            isLastMount.current = true;
        };
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [targetId]);

    useEffect(() => {
        if (isUpdateField && state.updated && state.updated !== state.previous) {
            const newTimeout = setTimeout((): void => {
                if (updateFunction && state.updated) {
                    updateFunction(refForm, state.updated as string);
                    dispatch({ type: "UPDATE_SUCCESS" });
                    setTimeout(() => {
                        dispatch({ type: "CLEAR_SUCCESS" });
                    }, 3000);
                }
            }, timerSetting);
            dispatch({ type: "SET_TIMEOUT", payload: newTimeout });
            return () => {
                if (isLastMount.current) {
                    clearTimeout(newTimeout);
                    updateFunction && updateFunction(refForm, state.updated as string);
                    isLastMount.current = false;
                }
            };
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [state.updated, state.previous]);

    const inputRegister = register && register(refForm, { required });
    return (
        <div className={`${className} w-full flex items-center justify-center relative`} data-testid="input-body">
            <label className="w-full flex justify-center">
                {label}
                <input
                    data-testid="input"
                    {...inputRegister}
                    className={`${inputClassName} w-full`}
                    defaultValue={defaultValue}
                    disabled={disabled}
                    onBlur={(e): void => {
                        onBlurCallBack && onBlurCallBack();
                        if (isUpdateField && state.previous !== e.target.value && !isError) {
                            dispatch({ type: "UPDATING", payload: e.target.value });
                            if (state.timeoutId) {
                                clearTimeout(state.timeoutId);
                            }
                        }
                    }}
                    onFocus={() => onFocusCallBack && onFocusCallBack()}
                    onChange={(e): void => {
                        onChangeCallBack && onChangeCallBack(e.target.value);
                        inputRegister && inputRegister.onChange(e);
                        if (isUpdateField) {
                            if (state.previous !== e.target.value) {
                                dispatch({ type: "SHOW_DOT" });
                            } else {
                                dispatch({ type: "CANCEL_UPDATE" });
                            }
                            if (state.timeoutId) {
                                clearTimeout(state.timeoutId);
                            }
                        }
                    }}
                    pattern={pattern}
                    placeholder={placeholder}
                    type={typeInput}
                />
            </label>
            <div className="w-5 absolute -right-4">
                {(isError || state.isCancelable || state.isSuccess) && (
                    <Dot
                        errorMessage={errorMessage}
                        isCancelable={state.isCancelable}
                        isCooldown={state.isCooldown}
                        isError={isError}
                        isSuccess={state.isSuccess}
                        isUpdateField={isUpdateField}
                        onClickCallback={(): void => {
                            if (setValue && clearErrors) {
                                setValue(refForm, state.previous);
                                clearErrors();
                            }
                            if (state.timeoutId) {
                                clearTimeout(state.timeoutId);
                            }
                            dispatch({ type: "CANCEL_UPDATE" });
                        }}
                        positionClassname={dotPosition}
                        trigger={state.trigger}
                    />
                )}
            </div>
        </div>
    );
};

export default Input;

