                    setValue={setValue}
                    targetId={
                        Math.round(dateAdd ? dateAdd.getTime() / 1000 : 0) ||
                        Math.round(date ? date.getTime() / 1000 : 0)
                    }
                    updateFunction={() => updateFunction && updateFunction(refForm, getValues(refForm))}
