    };
}

export function getRecipientsNameByIds(glpiUsers: IGlpiUsers[], ids: number[]): string[] {
    const recipients: string[] = [];
    ids.forEach((id) => {
        const recipient = glpiUsers.find((user) => id === user.id)?.name;
        if (recipient) {
            recipients.push(recipient);
        }
    });

    return recipients;
}

