import { formatDistanceToNowStrict, Locale, format } from "date-fns";
import { enUS, enGB, fr } from "date-fns/locale";
import { i18n } from "../../i18n";

export function getFormatedTimeToNow(date: string): string {
    const dateTicket = new Date(dateHandler(date));
    const formatedDate = formatDistanceToNowStrict(dateTicket).split(" ");
    return formatedDate[0] + formatedDate[1].charAt(0).toUpperCase();
}

export function getFormatedTimeToNowExtended(date: string, lang: string): string {
    const locale = getDateFnsLocaleFromUserLang(lang);
    const formatToDate = new Date(dateHandler(date));
    const timeToNow = formatDistanceToNowStrict(formatToDate, { addSuffix: true, locale: locale });
    return timeToNow;
}

export function getDateFnsLocaleFromUserLang(lang: string): Locale {
    switch (lang) {
        case "en_US":
            return enUS;
        case "fr_FR":
            return fr;
        case "en_GB":
            return enGB;
        default:
            return enGB;
    }
}

function dateHandler(date: string): string {
    if (!date.includes("T")) {
        return date.replace(" ", "T") + "+01:00";
    }

    return date;
}

export function formatDate(date: string, lang = "en_GB"): string {
    return format(new Date(dateHandler(date)), "P p", { locale: getDateFnsLocaleFromUserLang(lang) });
}

export function formatDateToNow(incomingDate: string, lang: string): string {
    let formatedDate: string;
    const date = new Date(dateHandler(incomingDate));
    const timestampDiff = (new Date().getTime() - new Date(date).getTime()) / 1000;
    const myLanguage = i18n.getFixedT(lang);
    const begin = myLanguage("formatDateToNow.begin");
    const middle = myLanguage("formatDateToNow.middle");
    const end = myLanguage("formatDateToNow.end");

    if (timestampDiff < 24 * 60 * 60 && date.getDay() === new Date().getDay()) {
        formatedDate = format(date, `p`, {
            locale: getDateFnsLocaleFromUserLang(lang),
        });
    } else if (timestampDiff < 7 * 24 * 60 * 60 && date.getDay() !== new Date().getDay()) {
        formatedDate = format(date, `eeee${middle}p`, {
            locale: getDateFnsLocaleFromUserLang(lang),
        });
    } else {
        formatedDate = format(date, `${begin}P${middle}p${end}`, {
            locale: getDateFnsLocaleFromUserLang(lang),
        });
    }
    return formatedDate;
}

export function getServiceStatusColor(status: number | undefined): string {
    switch (status) {
        case 200:
            return "green-400";
        case 202:
            return "blue-500";
        case 500:
            return "yellow-400";
        case 503:
            return "red-500";
        default:
            return "yellow-600";
    }
}

import { Status, Priority } from "../../enumeration";

interface ColorTypes {
    tailwind: string;
    hex: string;
}

interface Colors {
    [key: string]: ColorTypes;
}

export function getStatusColor(statusId: number, isHex: boolean): string {
    const statusColors: Colors = {
        new: {
            tailwind: "bg-neo-light-grey",
            hex: "#dae5e5",
        },
        pending: {
            tailwind: "bg-neo-yellow-sand",
            hex: "#E2DC8F",
        },
        assigned: {
            tailwind: "bg-neo-blue",
            hex: "#2fa8fc",
        },
        planned: {
            tailwind: "bg-neo-blue",
            hex: "#2fa8fc",
        },
        solved: {
            tailwind: "bg-neo-bg-A",
            hex: "#152535",
        },
        closed: {
            tailwind: "bg-neo-bg-A",
            hex: "#152535",
        },
    };

    const key: keyof Colors = Status[statusId].toLowerCase();
    return isHex ? statusColors[key].hex : statusColors[key].tailwind;
}

export function getPriorityColor(priorityId: number, isHex: boolean): string {
    const priorityColors: Colors = {
        verylow: {
            hex: "#7FEF7F",
            tailwind: "bg-neo-urgency-very-low",
        },
        low: {
            hex: "#B6C25D",
            tailwind: "bg-neo-urgency-low",
        },
        medium: {
            hex: "#ED943B",
            tailwind: "bg-neo-urgency",
        },
        high: {
            hex: "#EF713C",
            tailwind: "bg-neo-urgency-high",
        },
        veryhigh: {
            hex: "#F24D3D",
            tailwind: "bg-neo-urgency-very-high",
        },
        major: {
            hex: "#F42A3E",
            tailwind: "bg-neo-urgency-major",
        },
    };
    if (priorityId >= 1 && priorityId <= 6) {
        const key: keyof Colors = Priority[priorityId].toLowerCase();
        return isHex ? priorityColors[key].hex : priorityColors[key].tailwind;
    }
    return isHex ? "#FFFFFF" : "bg-neo-light-grey";
}

